import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faForward,
  faPlus,
  faPen,
  faTimes,
} from "@fortawesome/free-solid-svg-icons";
export default function Home() {
  const [deg, setDeg] = useState(0);
  const [sec, setSec] = useState(60);
  const [min, setMin] = useState(1);
  const [items, setItem] = useState<string[]>([]);
  const [title, setTitle] = useState("");

  useEffect(() => {
    let timer = setInterval(() => {
      if (deg >= 360) {
        resetTiming();
      } else {
        startTiming();
      }
      clearInterval(timer);
    }, 1000);
  }, [deg]);

  function startTiming() {
    setDeg(() => {
      return deg + 6;
    });
    setSec(sec - 1);
    setMin(0);
  }
  function stopTiming() {
    setDeg(deg);
  }
  function resetTiming() {
    setDeg(0);
    setMin(1);
    setSec(60);
  }
  function add() {
    setItem([...items, title]);
  }

  function handleTextfield(e: React.ChangeEvent<HTMLInputElement>) {
    setTitle(e.target.value);
  }

  return (
    <div className="home">
      <div className="title">
        <div className="title-flow">
          <FontAwesomeIcon icon={faForward} size={"5x"} />
          <h1>Please Add New</h1>

          <div className="todo-area">
            <input
              type="text"
              className="todo-area-input"
              onChange={handleTextfield}
            />
            <button type="button" className="todo-area-add" onClick={add}>
              <FontAwesomeIcon icon={faPlus}></FontAwesomeIcon>
            </button>
            <ul>
              {items.map((item) => (
                <li className="todo-item">
                  <input type="text" value={item} />
                  <div>
                    <button className="edit-btn">
                      <FontAwesomeIcon icon={faPen}></FontAwesomeIcon>
                    </button>
                    <button className="delete-btn">
                      <FontAwesomeIcon icon={faTimes}></FontAwesomeIcon>
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
      <div className="timer">
        <div className="circle"></div>
        <h1 className="time">{min > 0 ? "01:00" : "0" + min + ":" + sec}</h1>
        <div
          className="circle-inside"
          style={{
            transform: `rotate(${deg}deg)`,
          }}
        >
          <div className="dot"></div>
        </div>
      </div>
    </div>
  );
}
